var paper_key=data.paper_search_key;
var paper_num =data.paper_search_num;
var scholar_school=data.scholar_school;
var scholar_name=data.scholar_name;
var labels=[]  //个人画像的标签，目前考虑到的有：论文关键词、工作机构、学科专业、

labels.push({
  "name":scholar_school,
  "value":1,
});
labels.push({
  "name":scholar_name,
  "value":2,
});

for (i=0,len=paper_key.length;i<len;i++){
            labels.push({
                "name": paper_key[i],
                "value": paper_num[i],
            });
        }
var data1 = {
  value: labels,
        //专家图片，转码成base64
  image:"data:image/png;base64,"
      }
      $(function() {
          var myChart = echarts.init(document.getElementById('7_main'));
          //温馨提示：image 选取有严格要求不可过大；，否则firefox不兼容 iconfont上面的图标可以
          var maskImage = new Image();
          maskImage.src = data1.image

          maskImage.onload = function(){
              myChart.setOption( {
              backgroundColor:'#fff',
              tooltip: {
                show: false
              },
              series: [{
                type: 'wordCloud',
                gridSize: 1,
                sizeRange: [12, 55],
                rotationRange: [-45, 0, 45, 90],
                maskImage: maskImage,
                textStyle: {
                  normal: {
                    color: function() {
                      return 'rgb(' +
                          Math.round(Math.random() * 255) +
                          ', ' + Math.round(Math.random() * 255) +
                          ', ' + Math.round(Math.random() * 255) + ')'
                    }
                  }
                },
                left: 'center',
                top: 'center',
                // width: '96%',
                // height: '100%',
                right: null,
                bottom: null,
                // width: 300,
                // height: 200,
                // top: 20,
                data: data1.value
              }]
            })
          }
      })


